$ErrorActionPreference="Stop";$url="http://aka.ms/webpi-azps";$outdir="$($env:USERPROFILE)\Downloads\AzurePowershell";$outfile=[System.IO.Path]::Combine($outdir,(Split-Path -Leaf ([uri]::UnescapeDataString($url))));mkdir $outdir -Force|Out-Null;if(!(Test-Path $outfile)){$(New-Object Net.WebClient).DownloadFile($url, $outfile)};if(!(Test-Path $outfile)){throw "Download Failed: $($url)"};$md5="";if ($md5 -and ($md5 -notlike (Get-FileHash $outfile -Algorithm MD5).Hash)) {throw "MD5 check failed!"}; move-item $outfile "$($outfile).exe" -Force; & "$($outfile).exe";
