$ErrorActionPreference="Stop";[System.Net.ServicePointManager]::SecurityProtocol = "Ssl3";$url="http://www.voidtools.com/Everything-1.2.1.371.zip";$outdir="$($env:USERPROFILE)\Downloads\Everything";$outfile=[System.IO.Path]::Combine($outdir,(Split-Path -Leaf ([uri]::UnescapeDataString($url))));mkdir $outdir -Force|Out-Null;if(!(Test-Path $outfile)){$(New-Object Net.WebClient).DownloadFile($url, $outfile)};Start-Sleep -Seconds 2;if(!(Test-Path $outfile)){throw "Download Failed: $($url)"};$md5="753BC116DCB23BE1119C284F86193F51";if ($md5 -and ($md5 -notlike (Get-FileHash $outfile -Algorithm MD5).Hash)) {throw "MD5 check failed!"};$dest="$($env:USERPROFILE)\bin";if (!(Test-Path $dest -pathType container)){mkdir $dest -Force|Out-Null};(new-object -com shell.application).namespace($dest).CopyHere((new-object -com shell.application).namespace("$($outfile)").Items(),16);
