$ErrorActionPreference="Stop";$url="https://downloads.atlassian.com/software/sourcetree/windows/SourceTreeSetup_1.8.3.exe";$outdir="$($env:USERPROFILE)\Downloads\SourceTree";$outfile=[System.IO.Path]::Combine($outdir,(Split-Path -Leaf ([uri]::UnescapeDataString($url))));mkdir $outdir -Force|Out-Null;if(!(Test-Path $outfile)){$(New-Object Net.WebClient).DownloadFile($url, $outfile)};if(!(Test-Path $outfile)){throw "Download Failed: $($url)"};$md5="";if ($md5 -and ($md5 -notlike (Get-FileHash $outfile -Algorithm MD5).Hash)) {throw "MD5 check failed!"}; & $outfile /exenoui /exelog:"$($outfile).log" /quiet /qn /norestart /log "$($outfile).msi.log";
