$ErrorActionPreference="Stop";$url="https://download.sublimetext.com/Sublime%20Text%20Build%203126%20x64%20Setup.exe";$outdir="$($env:USERPROFILE)\Downloads\SublimeText3";$outfile=[System.IO.Path]::Combine($outdir,(Split-Path -Leaf ([uri]::UnescapeDataString($url))));mkdir $outdir -Force|Out-Null;if(!(Test-Path $outfile)){$(New-Object Net.WebClient).DownloadFile($url, $outfile)};if(!(Test-Path $outfile)){throw "Download Failed: $($url)"};$md5="";if ($md5 -and ($md5 -notlike (Get-FileHash $outfile -Algorithm MD5).Hash)) {throw "MD5 check failed!"}; & $outfile /VERYSILENT /SUPPRESSMSGBOXES /LOG="$($outfile).log";
