$ErrorActionPreference="Stop";[System.Net.ServicePointManager]::SecurityProtocol = "Ssl3";$url="http://download.sysinternals.com/files/SysinternalsSuite.zip";$outdir="$($env:USERPROFILE)\Downloads\SysInternals";$outfile=[System.IO.Path]::Combine($outdir,(Split-Path -Leaf ([uri]::UnescapeDataString($url))));mkdir $outdir -Force|Out-Null;if(!(Test-Path $outfile)){$(New-Object Net.WebClient).DownloadFile($url, $outfile)};Start-Sleep -Seconds 2;if(!(Test-Path $outfile)){throw "Download Failed: $($url)"};$md5="";if ($md5 -and ($md5 -notlike (Get-FileHash $outfile -Algorithm MD5).Hash)) {throw "MD5 check failed!"};$dest="$($env:USERPROFILE)\bin";if (!(Test-Path $dest -pathType container)){mkdir $dest -Force|Out-Null};(new-object -com shell.application).namespace($dest).CopyHere((new-object -com shell.application).namespace("$($outfile)").Items(),16);
